svg.script:
function text(x,y,str){
  var newText = document.createElementNS(svgNS,"text");
  newText.setAttributeNS(null,"x",x);     
  newText.setAttributeNS(null,"y",y); 
  newText.setAttributeNS(null,"font-size","100");
  newText.setAttributeNS(null,"text-anchor","middle");
  newText.setAttributeNS(null,"dominant-baseline","central");
  var textNode = document.createTextNode(str);
  newText.appendChild(textNode);
  document.getElementById("numbers").appendChild(newText);
}
function pathsCompile(){
  paths=svg.getElementsByTagName("path");
  paths.forEach(element.d=>{
    var newStr = str.replace(/\d?n(?:[+-]\d+)?/g, function(match) {
      element.setAttributeNS(null,"d",eval(match.split("").replace("2n","2*n")).join("");
    });
  }
}
function init(e){
  let params = (new URL(document.location)).searchParams;
  if ('n' in params === false) n = 11;
  else n=parseInt(params.n);
  if ('icon' in params === false) icon = "X"; //poomoji would be cooler
  else icon=params.icon;
  if ('layers' in params === false) layers = "all"; // /bottom/slider
  else layers=params.layers;
  // make the above safer?
  //
  // numbers and emoji
  for (let i = 0; i <= n; i++) {
    small=4.5;
    big=i+6.5;
    text(big,small,i);
    text(small,big,i);
    if (i>0){
      text(big,small-1,icon);
      text(small-1,big,icon);
    }
    for (let j = 0; j <= n; j++) {
      if ((i*j)>0) text(i+6.5,j+6.5,icon);
      text(i+n+7.5,j+n+7.5,i*j);
    }
  }
  pathsCompile();
}
